import { useState } from "react";

const NoteContainer = ({ setShowNav, selectedNote, setSelectedNote }) => {
  const expandNote = (noteId) => {
    console.log(noteId);
    setShowNav((prevState) => !prevState);
    // setSelectedNote(notes[noteId]);
    const newClassName = "gridSquare selectedNote";
    const foundNote = notes.find(note => {
      return note.id === noteId;
    });
    const newNoteToSet = { class: newClassName, ...foundNote };
    setNotes([newNoteToSet, ...notes ]);
  };

  console.log("selected note", selectedNote);
  const [notes, setNotes] = useState([
    {
      id: 1,
      title: "sample",
      subtitle: "sample also",
      class: "gridSquare unselectedNote",
    },
    {
      id: 2,
      title: "sample2",
      subtitle: "sample2 also",
      class: "gridSquare unselectedNote",
    },
  ]);
  return (
    <div id="noteContainer">
      {notes.map((note) => (
        <div
          className={note.class}
          id={`q${note.id}`}
          key={note.id}
          onClick={() => {
            expandNote(note.id);
          }}
        >
          <div id={`title${note.id}`}>{note.title}</div>
        </div>
      ))}

    </div>
  );
};

export default NoteContainer;
