import { useState } from "react";

const NoteContainer = ({ setShowNav, selectedNote, setSelectedNote }) => {
  const [selectedId, setSelectedId] = useState(null);
  const selectNote = (noteId) => {
    console.log(noteId);
    setShowNav((prevState) => !prevState);
    // setSelectedNote(notes[noteId]);
    setSelectedId(noteId);
  };

  console.log("selected note", selectedNote);
  const [notes, setNotes] = useState([
    {
      id: 1,
      title: "sample",
      subtitle: "sample also",
      class: "gridSquare",
    },
    {
      id: 2,
      title: "sample2",
      subtitle: "sample2 also",
      class: "gridSquare",
    },
  ]);
  return (
    <div id="noteContainer">
      {notes.map((note) => (
        <div
          // className={note.class}
          className={note.id === selectedId ? `${note.class} selectedNote` : `${note.class} unselectedNote`}
          id={`q${note.id}`}
          key={note.id}
          onClick={() => {
            selectNote(note.id);
          }}
        >
          <div id={`title${note.id}`}>{note.title}</div>
        </div>
      ))}

    </div>
  );
};

export default NoteContainer;
